import React from 'react';
import {
  Box,
  Typography,
  Paper,
  Switch,
  Slider,
  List,
  ListItem,
  ListItemText,
  Avatar,
  TextField,
} from '@mui/material';
import { styled } from '@mui/material/styles';
import PersonIcon from '@mui/icons-material/Person';
import DevicesIcon from '@mui/icons-material/Devices';
import AccessibilityNewIcon from '@mui/icons-material/AccessibilityNew';
import DarkModeIcon from '@mui/icons-material/DarkMode';
import TextFieldsIcon from '@mui/icons-material/TextFields';
import { UserProfile } from '../types';

const StyledPaper = styled(Paper)(({ theme }) => ({
  padding: theme.spacing(2),
  marginBottom: theme.spacing(2),
  background: theme.palette.mode === 'dark' 
    ? 'linear-gradient(45deg, #9c27b0 30%, #ba68c8 90%)'
    : 'linear-gradient(45deg, #2196f3 30%, #64b5f6 90%)',
  color: '#fff',
}));

interface SettingsPageProps {
  userProfile: UserProfile;
  onProfileChange: (profile: UserProfile) => void;
  darkMode: boolean;
  onDarkModeChange: (darkMode: boolean) => void;
  textSize: number;
  onTextSizeChange: (size: number) => void;
  deviceInfo: {
    name: string;
    model: string;
    version: string;
    lastUpdate: string;
  };
}

const SettingsPage: React.FC<SettingsPageProps> = ({
  userProfile,
  onProfileChange,
  darkMode,
  onDarkModeChange,
  textSize,
  onTextSizeChange,
  deviceInfo,
}) => {
  return (
    <Box sx={{ pb: 7 }}>
      <Typography variant="h4" gutterBottom>
        Настройки
      </Typography>

      <StyledPaper>
        <Box display="flex" alignItems="center" mb={2}>
          <PersonIcon sx={{ mr: 1 }} />
          <Typography variant="h6">Профиль пользователя</Typography>
        </Box>
        <Box display="flex" alignItems="center" mb={2}>
          <Avatar
            src={userProfile.avatar}
            alt={userProfile.name}
            sx={{ width: 64, height: 64, mr: 2 }}
          />
          <Box flex={1}>
            <TextField
              fullWidth
              label="Имя"
              value={userProfile.name}
              onChange={(e) => onProfileChange({ ...userProfile, name: e.target.value })}
              sx={{ mb: 1 }}
            />
            <TextField
              fullWidth
              label="Email"
              value={userProfile.email}
              onChange={(e) => onProfileChange({ ...userProfile, email: e.target.value })}
            />
          </Box>
        </Box>
      </StyledPaper>

      <StyledPaper>
        <Box display="flex" alignItems="center" mb={2}>
          <DevicesIcon sx={{ mr: 1 }} />
          <Typography variant="h6">Информация об устройстве</Typography>
        </Box>
        <List>
          <ListItem>
            <ListItemText
              primary="Название устройства"
              secondary={deviceInfo.name}
            />
          </ListItem>
          <ListItem>
            <ListItemText
              primary="Модель"
              secondary={deviceInfo.model}
            />
          </ListItem>
          <ListItem>
            <ListItemText
              primary="Версия"
              secondary={deviceInfo.version}
            />
          </ListItem>
          <ListItem>
            <ListItemText
              primary="Последнее обновление"
              secondary={deviceInfo.lastUpdate}
            />
          </ListItem>
        </List>
      </StyledPaper>

      <StyledPaper>
        <Box display="flex" alignItems="center" mb={2}>
          <AccessibilityNewIcon sx={{ mr: 1 }} />
          <Typography variant="h6">Режим для слабовидящих</Typography>
        </Box>
        <Box sx={{ px: 2 }}>
          <Typography gutterBottom>Размер текста</Typography>
          <Box display="flex" alignItems="center" gap={1}>
            <TextFieldsIcon />
            <Slider
              value={textSize}
              onChange={(_, value) => onTextSizeChange(value as number)}
              min={12}
              max={24}
              step={1}
              valueLabelDisplay="auto"
              sx={{ flex: 1 }}
            />
          </Box>
        </Box>
      </StyledPaper>

      <StyledPaper>
        <Box display="flex" alignItems="center" justifyContent="space-between">
          <Box display="flex" alignItems="center">
            <DarkModeIcon sx={{ mr: 1 }} />
            <Typography variant="h6">Темная тема</Typography>
          </Box>
          <Switch
            checked={darkMode}
            onChange={(e) => onDarkModeChange(e.target.checked)}
            color="primary"
          />
        </Box>
      </StyledPaper>
    </Box>
  );
};

export default SettingsPage;
