import React from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import { ThemeProvider, createTheme } from '@mui/material/styles';
import CssBaseline from '@mui/material/CssBaseline';
import Box from '@mui/material/Box';
import Container from '@mui/material/Container';
import Typography from '@mui/material/Typography';
import Grid from '@mui/material/Grid';
import Card from '@mui/material/Card';
import CardContent from '@mui/material/CardContent';
import Switch from '@mui/material/Switch';
import LightbulbIcon from '@mui/icons-material/Lightbulb';
import ThermostatIcon from '@mui/icons-material/Thermostat';
import SecurityIcon from '@mui/icons-material/Security';
import { styled } from '@mui/material/styles';

import BottomNav from './components/BottomNavigation';
import Dashboard from './components/Dashboard';
import LightsPage from './components/LightsPage';
import ThermostatPage from './components/ThermostatPage';
import SecurityPage from './components/SecurityPage';
import SettingsPage from './components/SettingsPage';
import { Room, DeviceInfo, UserProfile } from './types';

const theme = createTheme({
  palette: {
    mode: 'dark',
    primary: {
      main: '#90caf9',
    },
    background: {
      default: '#121212',
      paper: '#1e1e1e',
    },
  },
});

const StyledCard = styled(Card)(({ theme }) => ({
  height: '100%',
  display: 'flex',
  flexDirection: 'column',
  backgroundColor: theme.palette.background.paper,
  transition: 'transform 0.2s',
  '&:hover': {
    transform: 'scale(1.02)',
  },
}));

interface DeviceCardProps {
  title: string;
  icon: React.ReactNode;
  isOn: boolean;
  onToggle: () => void;
}

const DeviceCard: React.FC<DeviceCardProps> = ({ title, icon, isOn, onToggle }) => (
  <StyledCard>
    <CardContent>
      <Box display="flex" alignItems="center" justifyContent="space-between">
        <Box display="flex" alignItems="center" gap={1}>
          {icon}
          <Typography variant="h6">{title}</Typography>
        </Box>
        <Switch checked={isOn} onChange={onToggle} />
      </Box>
    </CardContent>
  </StyledCard>
);

// Моковые данные для демонстрации
const mockRooms: Room[] = [
  { id: '1', name: 'Гостиная', deviceId: 'LIGHT-001', isOn: true, brightness: 80, mode: 'auto' },
  { id: '2', name: 'Спальня', deviceId: 'LIGHT-002', isOn: false, brightness: 60, mode: 'night' },
  { id: '3', name: 'Кухня', deviceId: 'LIGHT-003', isOn: true, brightness: 100, mode: 'day' },
  { id: '4', name: 'Ванная', deviceId: 'LIGHT-004', isOn: false, brightness: 50, mode: 'auto' },
];

const mockCameras = [
  { id: '1', name: 'Камера 1', room: 'Гостиная', isActive: true, streamUrl: 'https://example.com/camera1.jpg' },
  { id: '2', name: 'Камера 2', room: 'Спальня', isActive: false, streamUrl: 'https://example.com/camera2.jpg' },
  { id: '3', name: 'Камера 3', room: 'Кухня', isActive: true, streamUrl: 'https://example.com/camera3.jpg' },
];

const mockDeviceInfo: DeviceInfo = {
  name: 'Smart Home Hub',
  model: 'SH-2023',
  version: '1.2.3',
  lastUpdate: '2023-05-15',
};

function App() {
  const [isDarkMode, setIsDarkMode] = React.useState(true);
  const [textSize, setTextSize] = React.useState(16);
  const [userProfile, setUserProfile] = React.useState<UserProfile>({
    name: 'Иван Иванов',
    email: 'ivan@example.com',
  });
  const [devices, setDevices] = React.useState({
    lights: true,
    thermostat: false,
    security: true,
    ac: false,
    router: true,
  });
  const [temperature, setTemperature] = React.useState(22);
  const [thermostatMode, setThermostatMode] = React.useState<'cool' | 'heat' | 'off'>('off');
  const [thermostatTimer, setThermostatTimer] = React.useState(0);
  const [currentRoom, setCurrentRoom] = React.useState('1');

  const theme = createTheme({
    palette: {
      mode: isDarkMode ? 'dark' : 'light',
      primary: {
        main: '#90caf9',
      },
      background: {
        default: isDarkMode ? '#121212' : '#f5f5f5',
        paper: isDarkMode ? '#1e1e1e' : '#ffffff',
      },
    },
    typography: {
      fontSize: textSize,
    },
  });

  const handleToggleDevice = (device: keyof typeof devices) => {
    setDevices((prev) => ({
      ...prev,
      [device]: !prev[device],
    }));
  };

  const handleToggleRoom = (roomId: string) => {
    // Здесь будет логика переключения комнаты
    console.log('Toggle room:', roomId);
  };

  const handleBrightnessChange = (roomId: string, value: number) => {
    // Здесь будет логика изменения яркости
    console.log('Change brightness:', roomId, value);
  };

  const handleModeChange = (roomId: string, mode: 'day' | 'night' | 'auto') => {
    // Здесь будет логика изменения режима
    console.log('Change mode:', roomId, mode);
  };

  const handleCameraSelect = (cameraId: string) => {
    console.log('Selected camera:', cameraId);
  };

  return (
    <ThemeProvider theme={theme}>
      <CssBaseline />
      <Router>
        <Box sx={{ minHeight: '100vh', pb: 7 }}>
          <Container maxWidth="lg">
            <Routes>
              <Route
                path="/"
                element={
                  <Dashboard
                    devices={devices}
                    stats={{
                      activeDevices: Object.values(devices).filter(Boolean).length,
                      totalDevices: Object.keys(devices).length,
                      energyUsage: 2.5,
                    }}
                    temperature={temperature}
                    onTemperatureChange={setTemperature}
                    onToggleDevice={handleToggleDevice}
                  />
                }
              />
              <Route
                path="/lights"
                element={
                  <LightsPage
                    rooms={mockRooms}
                    onToggleRoom={handleToggleRoom}
                    onBrightnessChange={handleBrightnessChange}
                    onModeChange={handleModeChange}
                  />
                }
              />
              <Route
                path="/thermostat"
                element={
                  <ThermostatPage
                    rooms={mockRooms}
                    currentRoom={currentRoom}
                    onRoomChange={setCurrentRoom}
                    temperature={temperature}
                    onTemperatureChange={setTemperature}
                    isOn={devices.thermostat}
                    onToggle={() => handleToggleDevice('thermostat')}
                    mode={thermostatMode}
                    onModeChange={setThermostatMode}
                    timer={thermostatTimer}
                    onTimerChange={setThermostatTimer}
                  />
                }
              />
              <Route
                path="/security"
                element={
                  <SecurityPage
                    cameras={mockCameras}
                    onCameraSelect={handleCameraSelect}
                  />
                }
              />
              <Route
                path="/settings"
                element={
                  <SettingsPage
                    isDarkMode={isDarkMode}
                    onThemeChange={() => setIsDarkMode(!isDarkMode)}
                    textSize={textSize}
                    onTextSizeChange={setTextSize}
                    userProfile={userProfile}
                    onProfileUpdate={setUserProfile}
                    deviceInfo={mockDeviceInfo}
                  />
                }
              />
            </Routes>
          </Container>
          <BottomNav />
        </Box>
      </Router>
    </ThemeProvider>
  );
}

export default App;
