import React from 'react';
import { ThemeProvider, createTheme } from '@mui/material/styles';
import CssBaseline from '@mui/material/CssBaseline';
import Box from '@mui/material/Box';
import Container from '@mui/material/Container';
import Typography from '@mui/material/Typography';
import Grid from '@mui/material/Grid';
import Card from '@mui/material/Card';
import CardContent from '@mui/material/CardContent';
import Switch from '@mui/material/Switch';
import LightbulbIcon from '@mui/icons-material/Lightbulb';
import ThermostatIcon from '@mui/icons-material/Thermostat';
import SecurityIcon from '@mui/icons-material/Security';
import { styled } from '@mui/material/styles';

const theme = createTheme({
  palette: {
    mode: 'dark',
    primary: {
      main: '#90caf9',
    },
    background: {
      default: '#121212',
      paper: '#1e1e1e',
    },
  },
});

const StyledCard = styled(Card)(({ theme }) => ({
  height: '100%',
  display: 'flex',
  flexDirection: 'column',
  backgroundColor: theme.palette.background.paper,
  transition: 'transform 0.2s',
  '&:hover': {
    transform: 'scale(1.02)',
  },
}));

interface DeviceCardProps {
  title: string;
  icon: React.ReactNode;
  isOn: boolean;
  onToggle: () => void;
}

const DeviceCard: React.FC<DeviceCardProps> = ({ title, icon, isOn, onToggle }) => (
  <StyledCard>
    <CardContent>
      <Box display="flex" alignItems="center" justifyContent="space-between">
        <Box display="flex" alignItems="center" gap={1}>
          {icon}
          <Typography variant="h6">{title}</Typography>
        </Box>
        <Switch checked={isOn} onChange={onToggle} />
      </Box>
    </CardContent>
  </StyledCard>
);

function App() {
  const [devices, setDevices] = React.useState({
    lights: false,
    thermostat: false,
    security: false,
  });

  const toggleDevice = (device: keyof typeof devices) => {
    setDevices(prev => ({
      ...prev,
      [device]: !prev[device],
    }));
  };

  return (
    <ThemeProvider theme={theme}>
      <CssBaseline />
      <Box sx={{ minHeight: '100vh', py: 4 }}>
        <Container maxWidth="lg">
          <Typography variant="h3" component="h1" gutterBottom align="center" sx={{ mb: 4 }}>
            Home App
          </Typography>
          <Grid container spacing={3}>
            <Grid item xs={12} md={4}>
              <DeviceCard
                title="Освещение"
                icon={<LightbulbIcon sx={{ fontSize: 30 }} />}
                isOn={devices.lights}
                onToggle={() => toggleDevice('lights')}
              />
            </Grid>
            <Grid item xs={12} md={4}>
              <DeviceCard
                title="Термостат"
                icon={<ThermostatIcon sx={{ fontSize: 30 }} />}
                isOn={devices.thermostat}
                onToggle={() => toggleDevice('thermostat')}
              />
            </Grid>
            <Grid item xs={12} md={4}>
              <DeviceCard
                title="Безопасность"
                icon={<SecurityIcon sx={{ fontSize: 30 }} />}
                isOn={devices.security}
                onToggle={() => toggleDevice('security')}
              />
            </Grid>
          </Grid>
        </Container>
      </Box>
    </ThemeProvider>
  );
}

export default App;
